<?php
    
    header("Access-Control-Allow-Origin: *");
    header("Access-Control-Allow-Methods: GET, POST, PUT, DELETE, OPTIONS");
    header("Access-Control-Allow-Headers: Content-Type, Authorization");
    
    if ($_SERVER['REQUEST_METHOD'] == 'OPTIONS') {
        http_response_code(200);
        exit();
    }
    
    session_start();

    require("conexion2.php");
    
    if(!isset($_SESSION["user_id"])){
        header('Location: https://plataforma.the-bizclub.com/inicioSesion.php');
    }
    
    if(isset($_SESSION["user_id"]) && $_SESSION["user_tipo"] != "Administrador"){
        header('Location: https://plataforma.the-bizclub.com/index.php');
    }

    // Buscando USER INCIADO
    
    $userId = $_SESSION["user_id"];

    $resultUserIni = $conn->query(
        "SELECT * FROM `$dbname`.`usuarios` 
        WHERE `usuarios`.`id_usuario` = $userId;"
    );

    $numRowsUserI = $resultUserIni->num_rows;
    $resultUserIni = $resultUserIni->fetch_assoc();

    //------------------------------------------------------

    $usuarios = [];

    if (isset($_GET['query'])) {

        $query = $conn->real_escape_string($_GET['query']);

        $result = $conn->query(
            "SELECT u.*, m.membre_nombre 
            FROM `soqkyjmy_bizclubPlataformaDb`.`usuarios` u
            LEFT JOIN `soqkyjmy_bizclubPlataformaDb`.`membresias` m ON u.user_membresia = m.id_membresia
            WHERE u.`user_nombre` LIKE '%$query%' 
            OR u.`user_apellido` LIKE '%$query%'"
        );

        if (!$result) {
            die("Error en la consulta: " . $conn->error);
        }

        while ($row = $result->fetch_assoc()) {
            $usuarios[] = $row;
        }

        header('Content-Type: application/json');
        echo json_encode($usuarios);
        exit; 

    }

